import React from "react";
import dice from "./dice.png";
import "./card.css";
const Card = (props) => {   
    return (
            <div className="card-body1 ">
                <div className="card-header1 ">
                    {/* {!props.image ? <img src="https://fakeimg.pl/260x150?text=Image+not+found&font=bebas" alt="news-image"/> : <img src={props.image} alt="dice" />} */}
                    <img src={props.image} alt="dice" />
                </div>
                <div className="card-footer1 ">
                    <p style={{fontWeight:"bold"}}>{props.heading}</p>
                    <p style={{fontSize:"10px"}}>{props.publisher} Â· {props.date}</p>
                    <p>{props.desc}</p>
                </div>
            </div>

    );

}

export default Card;
