            <div className="card-body1 ">
                <div className="card-header1 ">
                    {/* {!props.image ? <img src="https://fakeimg.pl/260x150?text=Image+not+found&font=bebas" alt="news-image"/> : <img src={props.image} alt="dice" />} */}
